    @Test
    public void intersection(){
        s = "zwaabbccaabbxt";
        String[] words = {"aa","bb","cc"};
        Integer[] expected = {2,4,6};
        Assert.assertArrayEquals( expected , solution.findSubstring(s,words).toArray(new Integer[0]));
    }

    @Test
    public void wordsLengthIsOne(){
        s = "xxabcxxbacxxcbaxx";
        String[] words = {"a","b","c"};
        Integer[] expected = {2,7,12};
        Assert.assertArrayEquals( expected , solution.findSubstring(s,words).toArray(new Integer[0]));
    }

    @Test
    public void similarWords(){
        s = "xbarbawxbawxbarx";
        String[] words = {"bar","baw"};
        Integer[] expected = {1};
        Assert.assertArrayEquals( expected , solution.findSubstring(s,words).toArray(new Integer[0]));
    }

    @Test
    public void shortWords(){
        s = "aba";
        String[] words = {"ab","ba"};
        Integer[] expected = {};
        Assert.assertArrayEquals( expected , solution.findSubstring(s,words).toArray(new Integer[0]));
    }

